<?phpfunction seo() { ?><?php if(is_category()) { ?><title><?php echo trim(wp_title('',0)); ?> | <?php bloginfo(name);?></title><meta content="<?php echo trim(strip_tags(category_description())); ?>" name="Description"/><meta content="<?php echo single_cat_title(); ?>" name="Keywords"/><?php } ?><?php if(is_single())  { ?><title><?php echo trim(wp_title('',0)); ?> | <?php bloginfo(name);?></title><?php }?><?php}add_action( 'v7v3_seo', 'seo');function dimox_breadcrumbs() {     $delimiter = '&raquo;';  $name = '首页';  $currentBefore = '<span>';  $currentAfter = '</span>';     if ( !is_home() && !is_front_page() || is_paged() ) {          global $post;    $home = get_bloginfo('url');    echo '<a href="' . $home . '">' . $name . '</a> ' . $delimiter . ' ';       if ( is_category() ) {      global $wp_query;      $cat_obj = $wp_query->get_queried_object();      $thisCat = $cat_obj->term_id;      $thisCat = get_category($thisCat);      $parentCat = get_category($thisCat->parent);      if ($thisCat->parent != 0) echo(get_category_parents($parentCat, TRUE, ' ' . $delimiter . ' '));      single_cat_title();      echo '' . $currentAfter;       } elseif ( is_day() ) {      echo '<a href="' . get_year_link(get_the_time('Y')) . '">' . get_the_time('Y') . '</a> ' . $delimiter . ' ';      echo '<a href="' . get_month_link(get_the_time('Y'),get_the_time('m')) . '">' . get_the_time('F') . '</a> ' . $delimiter . ' ';      echo $currentBefore . get_the_time('d') . $currentAfter;       } elseif ( is_month() ) {      echo '<a href="' . get_year_link(get_the_time('Y')) . '">' . get_the_time('Y') . '</a> ' . $delimiter . ' ';      echo $currentBefore . get_the_time('F') . $currentAfter;       } elseif ( is_year() ) {      echo $currentBefore . get_the_time('Y') . $currentAfter;       } elseif ( is_single() ) {      $cat = get_the_category(); $cat = $cat[0];      echo get_category_parents($cat, TRUE, ' ' . $delimiter . ' ');      echo $currentBefore;      the_title();      echo $currentAfter;       } elseif ( is_page() && !$post->post_parent ) {      echo $currentBefore;      the_title();      echo $currentAfter;       } elseif ( is_page() && $post->post_parent ) {      $parent_id  = $post->post_parent;      $breadcrumbs = array();      while ($parent_id) {        $page = get_page($parent_id);        $breadcrumbs[] = '<a href="' . get_permalink($page->ID) . '">' . get_the_title($page->ID) . '</a>';        $parent_id  = $page->post_parent;      }      $breadcrumbs = array_reverse($breadcrumbs);      foreach ($breadcrumbs as $crumb) echo $crumb . ' ' . $delimiter . ' ';      echo $currentBefore;      the_title();      echo $currentAfter;       } elseif ( is_search() ) {      echo $currentBefore . 'Search results for &#39;' . get_search_query() . '&#39;' . $currentAfter;       } elseif ( is_tag() ) {      single_tag_title();      echo '' . $currentAfter;       } elseif ( is_author() ) {       global $author;      $userdata = get_userdata($author);      echo $currentBefore . '当前文章页 ' . $userdata->display_name . $currentAfter;       } elseif ( is_404() ) {      echo $currentBefore . 'Error 404' . $currentAfter;    }       if ( get_query_var('paged') ) {      if ( is_category() || is_day() || is_month() || is_year() || is_search() || is_tag() || is_author() ) echo ' (';      echo __('Page') . ' ' . get_query_var('paged');      if ( is_category() || is_day() || is_month() || is_year() || is_search() || is_tag() || is_author() ) echo ')';    }             }}